StringBuilder nuxeoRequest = new StringBuilder();
nuxeoRequest.append("ecm:parentId = '").append(navigationPubInfos.getLiveId()).append("' ");

List levels = params.get("level");
if ((levels != null) && !levels.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String level : levels) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:levelsTree STARTSWITH '").append(level).append("' ");
}
nuxeoRequest.append(") ");
}

return nuxeoRequest.toString();

